using System; using System.Collections; using System.Collections.Generic; using System.Linq; using System.Text; using System.Threading.Tasks;  namespace lab.Tests {     [TestClass]     public class IsNullOrEmptyExtensionTests     {         [TestMethod]         public void IsNullOrEmptySourceIsNullTest()         {             IEnumerable source = null;                           bool result = source.IsNullOrEmpty();                           Assert.IsTrue(result);         }          [TestMethod]         public void IsNullOrEmptySourceIsEmptyTest()         {             IEnumerable source = new List<int>();                           bool result = source.IsNullOrEmpty();                           Assert.IsTrue(result);         }          [TestMethod]         public void IsNullOrEmptySourceIsNotEmptyTest()         {             IEnumerable source = new List<int> { 1, 2, 3 };                           bool result = source.IsNullOrEmpty();                           Assert.IsFalse(result);         }          [TestMethod]         public void IsNullOrEmptySourceContainsNullElementsTest()         {             IEnumerable source = new List<string> { "Раз", null, "Три" };                           bool result = source.IsNullOrEmpty();                           Assert.IsFalse(result);         }     } } 